let e=document.querySelector(".menu-burger__header"),t=document.querySelector(".header-list"),o=t.querySelectorAll(".header-list__item");e.addEventListener("click",function(){e.classList.toggle("menu-burger__header--active"),t.classList.toggle("header-list--active"),document.body.classList.toggle("stop-scroll")});o.forEach(function(s){s.addEventListener("click",function(){e.classList.remove("menu-burger__header--active"),t.classList.remove("header-list--active"),document.body.classList.remove("stop-scroll")})});const l=document.querySelector(".blog-nav__btn"),c=document.querySelector(".blog-nav"),r=document.querySelector(".blog-nav__close");l.addEventListener("click",()=>{c.classList.add("blog-nav--open")});r.addEventListener("click",()=>{c.classList.remove("blog-nav--open")});
