let e=document.querySelector(".menu-burger__header"),t=document.querySelector(".header-list"),r=t.querySelectorAll(".header-list__item");e.addEventListener("click",function(){e.classList.toggle("menu-burger__header--active"),t.classList.toggle("header-list--active"),document.body.classList.toggle("stop-scroll")});r.forEach(function(o){o.addEventListener("click",function(){e.classList.remove("menu-burger__header--active"),t.classList.remove("header-list--active"),document.body.classList.remove("stop-scroll")})});const n=document.querySelector(".search-btn"),c=document.querySelector(".search"),l=document.querySelector("body");n.addEventListener("click",()=>{c.classList.add("search--open")});l.addEventListener("click",()=>{c.classList.remove("search--open")});const a=document.querySelector(".blog-nav__btn"),s=document.querySelector(".blog-nav"),i=document.querySelector(".blog-nav__close");a.addEventListener("click",()=>{s.classList.add("blog-nav--open")});i.addEventListener("click",()=>{s.classList.remove("blog-nav--open")});
